import React, { useState, useEffect } from 'react';
import './App.css';

const BKK_API_BASE = 'https://futar.bkk.hu/api/query/v1/ws/otp/api/where';
const STOP_ID = 'F04797';

function App() {
  const [departures, setDepartures] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchDepartures = async () => {
    try {
      setLoading(true);
      setError(null);

      const response = await fetch(
        `${BKK_API_BASE}/arrivals-and-departures-for-stop.json?key=apaiary-test&version=3&appVersion=apiary-1.0&includeReferences=alerts&stopId=${STOP_ID}&minutesBefore=0&minutesAfter=30`
      );

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();

      if (data.status === 'OK' && data.data && data.data.entry) {
        const currentTime = Date.now();
        const filteredDepartures = data.data.entry.departures
          .filter(departure => {
            const departureTime = departure.predictedDepartureTime || departure.departureTime;
            const timeUntilDeparture = (departureTime - currentTime) / 1000 / 60; // minutes
            return timeUntilDeparture >= 0 && timeUntilDeparture <= 30;
          })
          .map(departure => {
            const departureTime = departure.predictedDepartureTime || departure.departureTime;
            const timeUntilDeparture = Math.round((departureTime - currentTime) / 1000 / 60);
            return {
              routeId: departure.routeId,
              headsign: departure.headsign,
              minutesUntil: timeUntilDeparture
            };
          })
          .sort((a, b) => a.minutesUntil - b.minutesUntil);

        setDepartures(filteredDepartures);
      } else {
        throw new Error('Invalid API response');
      }
    } catch (err) {
      setError(err.message);
      console.error('Error fetching departures:', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDepartures();
    const interval = setInterval(fetchDepartures, 30000); // Refresh every 30 seconds

    return () => clearInterval(interval);
  }, []);

  if (loading) {
    return (
      <div className="app">
        <h1>BKK Bus Tracker - F04797</h1>
        <p>Betöltés...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="app">
        <h1>BKK Bus Tracker - F04797</h1>
        <p className="error">Hiba történt: {error}</p>
        <button onClick={fetchDepartures}>Újrapróbálás</button>
      </div>
    );
  }

  return (
    <div className="app">
      <h1>BKK Bus Tracker - F04797</h1>
      <p className="subtitle">Következő 30 percben induló járatok</p>

      {departures.length === 0 ? (
        <p>Nincsenek induló járatok a következő 30 percben.</p>
      ) : (
        <table className="departures-table">
          <thead>
            <tr>
              <th>Járat</th>
              <th>Perc múlva</th>
            </tr>
          </thead>
          <tbody>
            {departures.map((departure, index) => (
              <tr key={index}>
                <td className="route">{departure.routeId}</td>
                <td className="time">{departure.minutesUntil} perc</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      <p className="last-updated">
        Utolsó frissítés: {new Date().toLocaleTimeString('hu-HU')}
      </p>
    </div>
  );
}

export default App;
