import React, { useState, useEffect } from 'react';
import './App.css';

const BKK_API_BASE = 'https://futar.bkk.hu/api/query/v1/ws/otp/api/where';
const STOP_ID = 'BKK_F04797';

function App() {
  const [departures, setDepartures] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchDepartures = async () => {
    try {
      setLoading(true);
      setError(null);

      // Try the real API first - let's try different endpoint formats
      let response;
      try {
        // First try the original format
        response = await fetch(
          `${BKK_API_BASE}/arrivals-and-departures-for-stop.json?key=ca61c2f4-982e-4460-aebd-950c15434919&version=3&appVersion=apiary-1.0&includeReferences=alerts&stopId=${STOP_ID}&minutesBefore=0&minutesAfter=30`,
          {
            method: 'GET',
            headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/json'
            }
          }
        );
      } catch (err) {
        // Try alternative endpoint format
        response = await fetch(
          `${BKK_API_BASE}/arrivals-and-departures-for-stop.json?key=ca61c2f4-982e-4460-aebd-950c15434919&version=3&appVersion=apiary-1.0&stopId=${STOP_ID}&minutesBefore=0&minutesAfter=30`,
          {
            method: 'GET',
            headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/json'
            }
          }
        );
      }

      if (!response.ok) {
        // Log the response for debugging
        console.log('Response status:', response.status);
        console.log('Response headers:', response.headers);

        if (response.status === 401) {
          // If unauthorized, use mock data for demonstration
          console.log('API key not valid, using mock data for demonstration');
          const mockDepartures = generateMockDepartures();
          setDepartures(mockDepartures);
          setError('API kulcs szükséges - demo adatok megjelenítése');
          return;
        } else if (response.status === 400) {
          // Try to get more details about the 400 error
          const errorText = await response.text();
          console.log('400 Error details:', errorText);
          throw new Error(`Bad Request (400): ${errorText}`);
        }
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();

      // Log the response structure for debugging
      console.log('API Response:', data);

      if (data.status === 'OK' && data.data && data.data.entry) {
        const currentTime = Date.now();

        // Check if stopTimes exist and is an array
        if (data.data.entry.stopTimes && Array.isArray(data.data.entry.stopTimes)) {
          const filteredDepartures = data.data.entry.stopTimes
            .filter(stopTime => {
              const departureTime = stopTime.predictedDepartureTime || stopTime.departureTime;
              const timeUntilDeparture = (departureTime - currentTime) / 1000 / 60; // minutes
              return timeUntilDeparture >= 0 && timeUntilDeparture <= 30;
            })
            .map(stopTime => {
              const departureTime = stopTime.predictedDepartureTime || stopTime.departureTime;
              const timeUntilDeparture = Math.round((departureTime - currentTime) / 1000 / 60);

              // Get route information from references
              const tripId = stopTime.tripId;
              const routeId = data.data.references.trips[tripId]?.routeId;
              const routeInfo = data.data.references.routes[routeId];

              return {
                routeId: routeInfo?.shortName || routeId || 'N/A',
                headsign: stopTime.stopHeadsign || 'N/A',
                minutesUntil: timeUntilDeparture
              };
            })
            .sort((a, b) => a.minutesUntil - b.minutesUntil);

          setDepartures(filteredDepartures);
          setError(null);
        } else {
          console.log('No stopTimes found or stopTimes is not an array');
          setDepartures([]);
          setError('Nincsenek induló járatok a következő 30 percben.');
        }
      } else {
        console.log('API response structure:', data);
        throw new Error(`Invalid API response: ${data.status || 'Unknown status'}`);
      }
    } catch (err) {
      // If any other error, use mock data
      console.log('API error, using mock data:', err.message);
      const mockDepartures = generateMockDepartures();
      setDepartures(mockDepartures);
      setError('API hiba - demo adatok megjelenítése');
    } finally {
      setLoading(false);
    }
  };

  const generateMockDepartures = () => {
    const routes = ['9', '15', '105', '115', '133E', '200E'];
    const departures = [];

    for (let i = 0; i < 8; i++) {
      const route = routes[Math.floor(Math.random() * routes.length)];
      const minutes = Math.floor(Math.random() * 30) + 1;

      departures.push({
        routeId: route,
        headsign: 'Központi pályaudvar',
        minutesUntil: minutes
      });
    }

    return departures.sort((a, b) => a.minutesUntil - b.minutesUntil);
  };

  useEffect(() => {
    fetchDepartures();
    const interval = setInterval(fetchDepartures, 30000); // Refresh every 30 seconds

    return () => clearInterval(interval);
  }, []);

  if (loading) {
    return (
      <div className="app">
        <h1>BKK Bus Tracker - F04797</h1>
        <p>Betöltés...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="app">
        <h1>BKK Bus Tracker - F04797</h1>
        <p className="error">Hiba történt: {error}</p>
        <button onClick={fetchDepartures}>Újrapróbálás</button>
      </div>
    );
  }

  return (
    <div className="app">
      <h1>BKK Bus Tracker - F04797</h1>
      <p className="subtitle">Következő 30 percben induló járatok</p>

      {departures.length === 0 ? (
        <p>Nincsenek induló járatok a következő 30 percben.</p>
      ) : (
        <table className="departures-table">
          <thead>
            <tr>
              <th>Járat</th>
              <th>Perc múlva</th>
            </tr>
          </thead>
          <tbody>
            {departures.map((departure, index) => (
              <tr key={index}>
                <td className="route">{departure.routeId}</td>
                <td className="time">{departure.minutesUntil} perc</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      <p className="last-updated">
        Utolsó frissítés: {new Date().toLocaleTimeString('hu-HU')}
      </p>
    </div>
  );
}

export default App;
